/* ---------------- TIME BY LEVEL ---------------- */
function getTime(level){
  if(level === "easy") return 15;
  if(level === "medium") return 25;
  if(level === "hard") return 60;
  return 15;
}

/* ---------------- UTILS ---------------- */
function shuffle(arr){
  return arr.sort(() => Math.random() - 0.5);
}

/* ---------------- START ---------------- */
startBtn.onclick = () => {
  const name = nameInput.value.trim();
  const level = levelSelect.value;
  const cls = classSelect.value;

  if(!name || !level || !cls){
    alert("नाम, Level और Class चुनो!");
    return;
  }

  const filtered = bank.filter(q => q.level === level && q.class === cls);

  if(filtered.length === 0){
    alert("इस level/class में सवाल नहीं हैं!");
    return;
  }

  questions = shuffle(filtered).slice(0, 20);

  index = 0;
  score = 0;
  scoreEl.textContent = 0;

  login.classList.add("d-none");
  quiz.classList.remove("d-none");
  load();
};

/* ---------------- LOAD QUESTION ---------------- */
function load(){
  if(index >= questions.length){
    end();
    return;
  }

  const q = questions[index];

  questionEl.textContent = `Q${index+1}. ${q.q}`;
  categoryEl.textContent = "Category: " + q.cat;

  optionsEl.innerHTML = "";
  q.options.forEach(opt => {
    const btn = document.createElement("button");
    btn.className = "option btn btn-outline-primary";
    btn.textContent = opt;
    btn.onclick = () => check(btn, opt, q.answer);
    optionsEl.appendChild(btn);
  });

  time = getTime(q.level);
  timerEl.textContent = time + "s";
  bar.style.width = "100%";

  clearInterval(timer);
  timer = setInterval(() => {
    time--;
    timerEl.textContent = time + "s";
    bar.style.width = (time / getTime(q.level) * 100) + "%";

    if(time <= 0){
      clearInterval(timer);
      showCorrectAnswer(q.answer);
      setTimeout(() => {
        index++;
        load();
      }, 1200);
    }
  }, 1000);
}

/* ---------------- CHECK ANSWER ---------------- */
function check(btn, sel, ans){
  clearInterval(timer);
  document.querySelectorAll(".option").forEach(b => b.disabled = true);

  if(sel === ans){
    btn.classList.add("btn-success");
    score += 10;
  } else {
    btn.classList.add("btn-danger");
    showCorrectAnswer(ans);
  }

  scoreEl.textContent = score;

  setTimeout(() => {
    index++;
    load();
  }, 1200);
}

function showCorrectAnswer(ans){
  document.querySelectorAll(".option").forEach(b => {
    if(b.textContent === ans) b.classList.add("btn-success");
    b.disabled = true;
  });
}

/* ---------------- END ---------------- */
function end(){
  clearInterval(timer);
  quiz.classList.add("d-none");
  result.classList.remove("d-none");
  finalEl.innerHTML = `<h4 class="text-center text-success">Your Score: ${score}</h4>`;
  saveScore();
  loadLeaderboard();
}

againBtn.onclick = () => location.reload();

/* ---------------- LEADERBOARD ---------------- */
function saveScore(){
  fetch("leaderboard_math.php", {
    method: "POST",
    headers: {"Content-Type":"application/json"},
    body: JSON.stringify({ name: nameInput.value.trim(), score })
  }).catch(()=>{});
}

function loadLeaderboard(){
  fetch("leaderboard_math.php")
    .then(r => r.json())
    .then(data => {
      const box = document.getElementById("leaderboard");
      box.innerHTML = "";
      data.forEach((u,i) => {
        const div = document.createElement("div");
        div.className = "lb-entry";
        div.innerHTML = `<b>${i+1}.</b> ${u.name} — ${u.score}`;
        box.appendChild(div);
      });
    })
    .catch(()=>{});
}
